const Search = () => {
  return (
    <div>
      <div className="header">
        <div>
          <Header></Header>
        </div>
        <NavBar></NavBar>
        <Content></Content>
      </div>
      <div className="search__content">
        <SearchPopup></SearchPopup>
        <SearchList></SearchList>
      </div>
      <div className="footer">
        <Footer></Footer>
      </div>
    </div>
  );
};

export default Search;
//================================
import { Header,NavBar,Content,Footer } from "../home/Home";
import searchLists from "../../data/search.json"
import "./dist/search.css"

//================================
const SearchPopup=()=>{
  return(
    <div className="search__popup">
      <h2>Search</h2>
      <p>Destination</p>
      <input ></input>
      <p >Check-in-Date</p>
      <input></input>
      <p>Option</p>
      <div>
        Min price per night
        <input></input>
        Max price per night
        Aldult
        <input></input>
        Children
        <input></input>
        Room
        <input></input>
      </div>
    </div>
  )
};
//==================
const SearchList =()=>{
  return(
    <div className="search__list">
      {
        searchLists.map(searchList=>(
          <RenderSearchList
          key={Math.random()}
          name={searchList.name}
          distance={searchList.distance}
          tag={searchList.tag}
          type={searchList.type}
          description={searchList.description}
          free_cancel={searchList.free_cancel}
          price={searchList.price}
          rate={searchList.rate}
          rate_text={searchList.rate_text}
          image_url={searchList.image_url}
          ></RenderSearchList>
        ))
      }
    </div>
  )
}

const RenderSearchList=(props)=>{
  return(
    <div className="search__list__col">
      <div className="col col-1">
        <img src={props.image_url}/>
      </div>
      <div className="col col-2">
        <h2>{props.name}</h2>
        <p>{`${props.distance} from center`}</p>
        <div><p>{props.tag}</p></div>
        <p><strong>{props.description}</strong></p>
        <p>{props.type}</p>
        {(props.free_cancel)&&(
          <div>
            <p><strong>Free cancellation</strong></p>
            <p>You can cancel later, so lock in thus great price today!</p>
          </div>
        )}
      </div>
      <div className="col col-3">
        <div>
          <h5>{props.rate_text}</h5>
          <h3>{props.rate}</h3>
        </div>
        <h2>{props.price}</h2>
        <p>Includes taxes and fees</p>
        <button className="btn btn-light">See availability</button>
      </div>
    </div>
  )
}
