const Home = (props) => {
  return (
    <div>
      <div className="header">
        <div>
          <Header></Header>
        </div>
        <NavBar></NavBar>
        <Content></Content>
      </div>
      <div className="content">
        <City></City>
        <ContentType></ContentType>
        <ContentHotel></ContentHotel>

      </div>
      <FormInput></FormInput>
      <div className="footer">

      </div>
    </div>

  );
};

export default Home;
