import Searchcontext from "./contextSearch";
import { useEffect, useState, useReducer } from "react";

// const defaultsearchState = { inputSearch: "" };
// const searchReducer = (state, action) => {
//   switch (action.type) {
//     case "ADD":
//       return { inputSearch: action.inputSearch };
//     case "REMOVE":
//       return { inputSearch: state.inputSearch };
//     default:
//       return defaultsearchState;
//   }
// };

const SearchProvider = (props) => {
  const [inputSearchHandler, setinputSearchHandler] = useState("");
  //   const [searchState, dispatchsearchAction] = useReducer(
  //     searchReducer,
  //     defaultsearchState
  //   );

  const btnSearchhHandler = (item) => {
    // dispatchsearchAction({  inputSearch: item ,type: "ADD"});
    setinputSearchHandler(item);
  };

  const btnResethHandler = (item) => {
    setinputSearchHandler(item);
    
  };
  const searchContext = {
    // inputSearch: searchState.inputSearch,
    inputSearch: inputSearchHandler,
    btnSearch: btnSearchhHandler,
    btnReset: btnResethHandler,
  };
  return (
    <Searchcontext.Provider value={searchContext}>
      {props.children}
    </Searchcontext.Provider>
  );
};

export default SearchProvider;
