import React, { useState, useEffect, useContext ,useRef} from "react";
import RenderMoviesList from "./RenderMoviesList";
import ClickContext from "../store/ClickContext";
import MovieDetail from "../MovieDetail/MovieDetail";

//==================
const reducer = (state, action) => {
  switch (action) {
    case "fetchTrending":
      return "Xu hướng";
    case "fetchTopRated":
      return "Xếp hạng cao";
    case "fetchActionMovies":
      return "Hành động";
    case "fetchComedyMovies":
      return "Hài";
    case "fetchHorrorMovies":
      return "Kinh dị";
    case "fetchRomanceMovies":
      return "Lãng mạn";
    case "fetchDocumentaries":
      return "tài liệu";
    default:
      return "";
  }
};
const MoviesList = (props) => {
  const thinh = useContext(ClickContext);

  if (thinh.clickTF) {
    console.log(thinh);
  }

  const reducer = (title) => {
    switch (title) {
      case "fetchTrending":
        return "Xu hướng";
      case "fetchTopRated":
        return "Xếp hạng cao";
      case "fetchActionMovies":
        return "Hành động";
      case "fetchComedyMovies":
        return "Hài";
      case "fetchHorrorMovies":
        return "Kinh dị";
      case "fetchRomanceMovies":
        return "Lãng mạn";
      case "fetchDocumentaries":
        return "Tài liệu";
      default:
        return "";
    }
  };
  const handler=(dataChil)=>{
    console.log(dataChil)
  }
  RenderMoviesList(handler)
  return (
    <React.Fragment>
      <h5>{reducer(props.title)}</h5>
      <div
        className={`${
          props.title === "fetchNetflixOriginals"
            ? "contentFirst contentAll"
            : "contentAll"
        }`}
      >
        {props.data.map((data) => {
          return (
            <RenderMoviesList
              key={data.id}
              id={data.id}
              imgPoster={`
              https://image.tmdb.org/t/p/original${
                props.title === "fetchNetflixOriginals"
                  ? data.poster_path
                  : data.backdrop_path
                  ? data.backdrop_path
                  : data.poster_path
              }`}
              name={data.title ? data.title : data.name}
              releaseDate={data.release_date}
              depcription={data.overview}
              vote={data.vote_average}
            />
          );
        })}
      </div>
     <div ></div>
    </React.Fragment>
  );
};
export default MoviesList;
