import APIContext from "./APIContext";
import useFetchApi from "./../../hooks/use-fetchApi";

import { useEffect, useState } from "react";

//================
const ContextProvider = (props) => {
  const [dataList, setdataList] = useState([]);

  const API_KEY = "6fc3d765745ff0bf0840cc0f79c98ab6";
  const HttpApi = "https://api.themoviedb.org/3";
  const requests = {
    fetchTrending: `/trending/all/week?api_key=${API_KEY}&language=en-US`,
    fetchNetflixOriginals: `/discover/tv?api_key=${API_KEY}&with_network=123`,
    fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US`,
    fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28`,
    fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35`,
    fetchHorrorMovies: `/discover/movie?api_key=${API_KEY}&with_genres=27`,
    fetchRomanceMovies: `/discover/movie?api_key=${API_KEY}&with_genres=10749`,
    fetchDocumentaries: `/discover/movie?api_key=${API_KEY}&with_genres=99`,
    fetchSearch: `/search/movie?api_key=${API_KEY}&language=en-US`,
  };

  useEffect(() => {
    for (const key in requests) {
      const { data, isLoading, error } = useFetchApi(
        `${HttpApi}${requests[key]}`
        
      );
      setdataList(data)
    }
  }, []);

  const context = {
    API_KEY: API_KEY,
    HttpImg: "https://image.tmdb.org/t/p/original",
    link_API: data,
  };
  return (
    <APIContext.Provider value={context}>{props.children}</APIContext.Provider>
  );
};
export default ContextProvider;
