  // console.log(dataHearder)
  if (LoadingHendler) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  } else console.log(dataHearder);
  return (
    <RenderHeader
      key={dataHearder.id}
      imgBackground={dataHearder.backdrop_path}
      title={dataHearder.original_title}
      depcription={dataHearder.overview}
    ></RenderHeader>
  );
};
export default Header;
