import React, { useEffect, useState } from "react";
// import Example from "./ReactYoutube";
//===========================
const MovieDetail = (props) => {
  const [dataMoviesId, setdataMoviesId] = useState([]);
  const [isLoading, setisLoading] = useState(true);
  const [error, seterror] = useState(null);
  const [checkKeyImg, setcheckKeyImg] = useState([]);
  useEffect(() => {
    // const getData = [];

    const fetchApi = async () => {
      setisLoading(true);

      const response = await fetch(
        `https://api.themoviedb.org/3//movie/${props.id}/videos?api_key=6fc3d765745ff0bf0840cc0f79c98ab6`
      );

      const data = await response.json();

      setdataMoviesId(data);
      setisLoading(false);
    };

    fetchApi().catch((error) => {
      setisLoading(false);
      seterror(error.message);
    });
   
  }, []);
  if (isLoading) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (error) {
    return (
      <section>
        <p>{data.error}</p>
      </section>
    );
  }
  
  const a = dataMoviesId.results.find(
    (b) => b.site === "YouTube" && b.type === "Trailer"
  );
  const c = dataMoviesId.results.find(
    (d) => d.site === "YouTube" && d.type === "Teaser"
  );
 
  (!a&!c?console.log("no"):'')
  
  
  return (
    <div className="movie_detail">
      <div className="depcrip">
        <h3>{props.name}</h3>
        <h6><strong>Release Date: {props.date}</strong></h6>
        <h6><strong>Vote: {props.vote} / 10</strong></h6>
        <p>{props.depcrip}</p>
      </div>
      {!a&&!c?<img width="560"
        height="315"src={`https://image.tmdb.org/t/p/original${props.srcImg}`} alt={props.title}/>:<iframe
        width="560"
        height="315"
        src={`https://www.youtube.com/embed/${a.key?a.key:c.key}`}
        title="YouTube video player"
        frameBorder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
      ></iframe>}
      
    </div>
  );
};
export default MovieDetail;
