import RenderHeader from "./RenderHeader";
// import "../../dist/index.css"
import React from "react";
import useFetchApi from "../../hooks/use-fetchApi";
//=========================
const Header = () => {
  const API_KEY = "6fc3d765745ff0bf0840cc0f79c98ab6";
  const requests = {
    fetchTrending: `/trending/all/week?api_key=${API_KEY}&language=en-US`,
    fetchNetflixOriginals: `/discover/tv?api_key=${API_KEY}&with_network=123`,
    fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US`,
    fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28`,
    fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35`,
    fetchHorrorMovies: `/discover/movie?api_key=${API_KEY}&with_genres=27`,
    fetchRomanceMovies: `/discover/movie?api_key=${API_KEY}&with_genres=10749`,
    fetchDocumentaries: `/discover/movie?api_key=${API_KEY}&with_genres=99`,
    fetchSearch: `/search/movie?api_key=${API_KEY}&language=en-US`,
    abc: `https://api.themoviedb.org/3//movie/718930/videos?api_key=6fc3d765745ff0bf0840cc0f79c98ab6&language=en-US`,
  };
  const http = "https://api.themoviedb.org/3";

  const API_header = `https://api.themoviedb.org/3/movie/550?api_key=${API_KEY}`;
  const {
    data: dataHearder,
    isLoading: LoadingHendler,
    error: errorHeader,
  } = useFetchApi(`${http}${requests.fetchTrending}`);
  //   console.log(dataHearder.results[0])
  // const[dataHearder,setdataHearder]=useState('')
  // useEffect(()=>{
  //     const fetchApi= async()=>{
  //         const response =await fetch(`https://api.themoviedb.org/3/movie/550?api_key=${API_KEY}`)
  //         const data = await response.json();
  //         console.log(data)
  //         setdataHearder(data)
  //     }
  //     fetchApi()

  // },[])

  const { data: datafetchTrending, isLoading: is12 } = useFetchApi(
    `${http}${requests.fetchTrending}`
  );
  const { data: datafecth, isLoading, error } = useFetchApi(requests.abc);
  console.log(datafecth);
  console.log(datafetchTrending);
  //dong 35 a nhe

  // console.log(requests)
  // console.log(dataHearder)
  if (LoadingHendler) {
    console.log(1);
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (errorHeader) {
    return (
      <section>
        <p>{errorHeader}</p>
      </section>
    );
  }
  const numberData = Math.floor(Math.random() * dataHearder.results.length - 1);
  console.log(dataHearder.results[numberData].original_title)
  return (
    <RenderHeader
      key={dataHearder.results[numberData].id}
      imgBackground={dataHearder.results[numberData].backdrop_path}
      title={dataHearder.results[numberData].original_title?dataHearder.results[numberData].original_title:dataHearder.results[numberData].original_title}
      depcription={dataHearder.results[numberData].overview}
      load={LoadingHendler}
    ></RenderHeader>
  );
};
export default Header;
