import React from "react";
import RenderContentFourth from "./RenderContentFourth";
import useFetchApi from "../../hooks/use-fetchApi";
import { useContext } from "react";
import APIContext from "../store/APIContext";


//==================

const ContentFourth = () => {
  const dataAPI = useContext(APIContext);
  const http = "https://api.themoviedb.org/3";

  const {
    data: dataContentFourth,
    isLoading: LoadingContentFourth,
    error: errorContentFourth,
  } = useFetchApi(`${http}${dataAPI.link_API.fetchActionMovies}`);

  if (LoadingContentFourth) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (errorContentFourth) {
    return (
      <section>
        <p>{errorContentFourth}</p>
      </section>
    );
  }
  // dataContentFourth.results.map(a=>{
  //   console.log(`${dataAPI.HttpImg}${a.poster_path}`)
  // })

  return (
    <React.Fragment>
      <h5>Xu hướng</h5>
      <div className="contentFourth">
        {dataContentFourth.results.map((data) => {
          return (
            <RenderContentFourth
              key={data.id}
              imgPbackdrop={`${dataAPI.HttpImg}${data.backdrop_path}`}
              name={data.title ? data.title : data.name}
              releaseDate={data.release_date}
              depcription={data.overview}
              vote={data.vote_average}
            />
          );
        })}
      </div>
    </React.Fragment>
  );
};
export default ContentFourth;
