import RenderResult from "./RenderResult";
import { useContext ,useEffect,useState} from "react";
import APIContext from "../store/APIContext";
//======================

const ResultList = () => {
  const dataSearch = useContext(APIContext);
  const[keySearch,setkeySearch]=useState('thor')
  const [dataApimovie, setdataApimovie] = useState([]);
  
  
  useEffect(() => {
    

    const fetchApi = async () => {
      
      
        
        const response = await fetch(`
        https://api.themoviedb.org/3/search/movie?api_key=6fc3d765745ff0bf0840cc0f79c98ab6&language=en-US&query=${keySearch}&page=1&include_adult=true`);

        const data = await response.json();
       
        
      
      
      setdataApimovie(data);
      
    };
    fetchApi
  }, []);
  console.log(dataApimovie.results)
  
  

  
  return (
    <div className="form_result">
      <h5>Search Result</h5>
      <div className="result_img">
        {dataApimovie.results.map((a) => (
          <RenderResult
            key={a.id}
            imgPoster={`
              https://image.tmdb.org/t/p/original${
                a.poster_path ? a.poster_path : a.backdrop_path
              }`}
          ></RenderResult>
        ))}
      </div>
    </div>
  );
};
export default ResultList;
