import React, { useEffect, useState } from "react";
// import Example from "./ReactYoutube";
//===========================
const MovieDetail = (props) => {
  const [dataMoviesId, setdataMoviesId] = useState([]);
  const [isLoading, setisLoading] = useState(true);
  const [error, seterror] = useState(null);
  const [checkKeyImg, setcheckKeyImg] = useState("");
  useEffect(() => {
    // const getData = [];

    const fetchApi = async () => {
      setisLoading(true);

      const response = await fetch(
        `https://api.themoviedb.org/3//movie/${props.id}/videos?api_key=6fc3d765745ff0bf0840cc0f79c98ab6`
      );

      const data = await response.json();

      setdataMoviesId(data);
      setisLoading(false);
    };

    fetchApi().catch((error) => {
      setisLoading(false);
      seterror(error.message);
    });
  }, []);
  if (isLoading) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (error) {
    return (
      <section>
        <p>{data.error}</p>
      </section>
    );
  }
  const a =dataMoviesId.results.find((b)=>b.site === "YouTube"&&b.type==="Trailer");
  const c =dataMoviesId.results.find((d)=>d.site === "YouTube"&&d.type==="Teaser");
  setcheckKeyImg(c);
  setcheckKeyImg(a);
  console.log()
  // let keyTrailer, keyTeaser;
  // const checkContent = () => {
  //   dataMoviesId.results.map((result) => {
  //     if (result.site === "YouTube") {
  //       console.log(result.key, result.tite);
  //       if (result.type === "Trailer") {
  //         console.log(result.key, result.tite, 1);
  //         return keyTrailer = result.key;

            
          
  //       } else if (result.type === "Teaser") {
  //         console.log(result.key, result.tite);
  //         return keyTeaser = result.key;
  //       }
  //     }
  //   });
  //   setcheckKeyImg((keyTrailer?keyTrailer:keyTeaser))
  // };

  // checkContent();
  // console.log(checkKeyImg)
  return (
    <div className="abc">
      <iframe
        width="560"
        height="315"
        src={`https://www.youtube.com/embed/${dataMoviesId.results[0].key}`}
        title="YouTube video player"
        frameBorder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
      ></iframe>
      {/* <Example id={dataMoviesId.results[0].key}></Example> */}
    </div>
  );
};
export default MovieDetail;
