import React from "react";
import RenderContentImg from "./RenderContentImg";
import useFetchApi from "../../hooks/use-fetchApi";
import { useContext } from "react";
import APIContext from "../store/APIContext";



//==================

const ContentSeventh = () => {
  const dataAPI = useContext(APIContext);
  const http = "https://api.themoviedb.org/3";

  const {
    data: dataContentSeventh,
    isLoading: LoadingContentSeventh,
    error: errorContentSeventh,
  } = useFetchApi(`${http}${dataAPI.link_API.fetchRomanceMovies}`);

  if (LoadingContentSeventh) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (errorContentSeventh) {
    return (
      <section>
        <p>{errorContentSeventh}</p>
      </section>
    );
  }
  // dataContentSeventh.results.map(a=>{
  //   console.log(`${dataAPI.HttpImg}${a.poster_path}`)
  // })

  return (
    <React.Fragment>
      <h5>lãng mạn</h5>
      <div className="contentSeventh contentAll">
        {dataContentSeventh.results.map((data) => {
          return (
            <RenderContentImg
              key={data.id}
              id={data.id}
              imgPbackdrop={`${dataAPI.HttpImg}${data.backdrop_path}`}
              name={data.title ? data.title : data.name}
              releaseDate={data.release_date}
              depcription={data.overview}
              vote={data.vote_average}
            />
          );
        })}
      </div>
    </React.Fragment>
  );
};
export default ContentSeventh;
