import React from "react";
import RenderContentSecond from "./RenderContentSecond";
import useFetchApi from "../../hooks/use-fetchApi";
import { useContext } from "react";
import APIContext from "../store/APIContext";
const ContentSecond=()=>{
    const dataAPI=useContext(APIContext)
    const http = "https://api.themoviedb.org/3";
      
        const {
          data: dataContentSecond,
          isLoading: LoadingContentSecond,
          error: errorContentSecond,
        } = useFetchApi(`${http}${dataAPI.link_API.fetchNetflixOriginals}`);
        
        if (LoadingContentSecond) {
          
          return (
            <section>
              <p>Loading........</p>
            </section>
          );
        }
        if (errorContentSecond) {
          return (
            <section>
              <p>{errorContentSecond}</p>
            </section>
          );
        }
        // dataContentSecond.results.map(a=>{
        //   console.log(`${dataAPI.HttpImg}${a.poster_path}`)
        // })
        
        return(
          <div className="contentSecond">
                {dataContentSecond.results.map((data)=>{
                  return(
                  <RenderContentSecond
                    key={data.id}
                    imgPbackdrop={`${dataAPI.HttpImg}${data.backdrop_path}`}
                    name={data.title?data.title:data.name}
                    releaseDate={data.release_date}
                    depcription={data.overview}
                    vote={data.vote_average}
                  />
                  )
                 })}
          </div>
            
               
            
        )
    
    };
    export default ContentSecond;
