import React,{useEffect} from "react";


const MovieDetail = (props) => {
  useEffect(() => {
    const getData = [];

    const fetchApi = async () => {
      setisLoading(true);
      for (const key in requests) {
        if (key === "fetchSearch") {
          continue;
        }
        const response = await fetch(`https://api.themoviedb.org/3//movie/${props.id}/videos?api_key=6fc3d765745ff0bf0840cc0f79c98ab6`);

        const data = await response.json();
        data.key = key;
        getData.push(data);
      }
      setisLoading(false);

      setdataApimovie(getData);
    };
    fetchApi().catch((error) => {
      setisLoading(false);
      seterror(error.message);
    });
  }, []);

