import React from "react";
import RenderContentImg from "./RenderContentImg";
import useFetchApi from "../../hooks/use-fetchApi";
import { useContext } from "react";
import APIContext from "../store/APIContext";



//==================

const ContentSixth = () => {
  const dataAPI = useContext(APIContext);
  const http = "https://api.themoviedb.org/3";

  const {
    data: dataContentSixth,
    isLoading: LoadingContentSixth,
    error: errorContentSixth,
  } = useFetchApi(`${http}${dataAPI.link_API.fetchHorrorMovies}`);

  if (LoadingContentSixth) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (errorContentSixth) {
    return (
      <section>
        <p>{errorContentSixth}</p>
      </section>
    );
  }
  // dataContentSixth.results.map(a=>{
  //   console.log(`${dataAPI.HttpImg}${a.poster_path}`)
  // })

  return (
    <React.Fragment>
      <h5>Kinh dá»‹</h5>
      <div className="contentSixth contentAll">
        {dataContentSixth.results.map((data) => {
          return (
            <RenderContentImg
              key={data.id}
              id={data.id}
              imgPbackdrop={`${dataAPI.HttpImg}${data.backdrop_path}`}
              name={data.title ? data.title : data.name}
              releaseDate={data.release_date}
              depcription={data.overview}
              vote={data.vote_average}
            />
          );
        })}
      </div>
    </React.Fragment>
  );
};
export default ContentSixth;
