import React,{useState} from "react";
import IconSearch from "./IconSearch";

//==========================
const RenderHeader = (props) => {
  const [navbar,setNavbar]=useState(false)
  
  const changeBackground=()=>{
    if(window.scrollY>=100){
      setNavbar(true)
    }
    else{
      setNavbar(false)
    }
  }
  window.addEventListener('scroll',changeBackground);
  return (
    
    <div
    
    >
        <img src={`https://image.tmdb.org/t/p/original${props.imgBackground}`} alt={props.title}/>
      <div className={NavBar__first}>
        <h2>Movie App</h2>
        <IconSearch></IconSearch>
      </div>
      <div className="NavBar__second">
        <h1>{props.title}</h1>
        <button className="btn btn-light">Play</button>
        <button className="btn btn-light">My list</button>
        <p>
          <strong>{props.depcription}</strong>
        </p>
      </div>
    </div>
  );
};

export default RenderHeader;
