import React, { useEffect, useState,useContext } from "react";
import Example from "./ReactYoutube";
const MovieDetail = (props) => {
  const [dataMoviesId, setdataMoviesId] = useState([]);
  const [isLoading, setisLoading] = useState(true);
  const [error, seterror] = useState(null);
  useEffect(() => {
    // const getData = [];

    const fetchApi = async () => {
      setisLoading(true);

      const response = await fetch(
        `https://api.themoviedb.org/3//movie/${props.id}/videos?api_key=6fc3d765745ff0bf0840cc0f79c98ab6`
      );

      const data = await response.json();
      
      setdataMoviesId(data);
      setisLoading(false);
    };
    

    fetchApi().catch((error) => {
      setisLoading(false);
      seterror(error.message);
    });
  }, []);
  if (isLoading) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (error) {
    return (
      <section>
        <p>{data.error}</p>
      </section>
    );
  }

  console.log(dataMoviesId.results.indexOf("site"))
  
 
  return (
    <div className="abc">
      <iframe
        width="560"
        height="315"
        src={`https://www.youtube.com/embed/${dataMoviesId.results[0].key}`}
        title="YouTube video player"
        frameBorder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
      ></iframe>
      <Example ></Example>

    </div>
  );
};
export default MovieDetail;
