import MovieDetail from "../MovieDetail/MovieDetail";
import ClickContext from "../store/ClickContext";
//=====================
const RenderMoviesList = (props) => {
  const [onClickImg,setonClickImg]=useState(false)
  const abc=useContext(ClickContext)
  // console.log(abc)
  
    const clickImg=()=>{
        
        setonClickImg(!onClickImg)
        abc.id=props.id
        abc.clickTF=onClickImg
        abc.clickImg([])
        
    }
  return (
    <div className={props.name}>
      <img
        src={props.imgPoster}
        alt={props.name}
        onClick={clickImg}
      />

      {/* {onClickImg &&<MovieDetail
      id={props.id} 
      ></MovieDetail>} */}
    </div>
  );
};
export default RenderMoviesList;
