import React, { useReducer, useEffect } from "react";
import RenderContentImg from "./RenderContentImg";

//==================
const reducer = (state, action) => {
  switch (action) {
    case "fetchTrending":
      return "Xu hướng";
    case "fetchTopRated":
      return "Xếp hạng cao";
    case "fetchActionMovies":
      return "Hành động";
    case "fetchComedyMovies":
      return "Hài";
    case "fetchHorrorMovies":
      return "Kinh dị";
    case "fetchRomanceMovies":
      return "Lãng mạn";
    case "fetchDocumentaries":
      return "tài liệu";
  }
};

const MoviesList = (props) => {
  const [chaneTitle, dispatch] = useReducer(reducer, "");
  console.log(props);
  const dataAPI = "https://image.tmdb.org/t/p/original";
  useEffect(() => {}, []);

  return (
    <React.Fragment>
      <h5>{chaneTitle}</h5>
      <div className="contentAll">
        {props.data.map((data) => {
          return (
            <RenderContentImg
              key={data.id}
              id={data.id}
              imgPoster={`${dataAPI}${
                data.backdrop_path ? data.backdrop_path : data.poster_path
              }`}
              name={data.title ? data.title : data.name}
              releaseDate={data.release_date}
              depcription={data.overview}
              vote={data.vote_average}
            />
          );
        })}
      </div>
    </React.Fragment>
  );
};
export default MoviesList;
