import APIContext from "../store/APIContext";
import { useContext } from "react";

import RenderContentImg from "./RenderContentImg";
//======================
const ContentFirst=()=>{
const dataAPI=useContext(APIContext)
const http = "https://api.themoviedb.org/3";
  
    const {
      data: dataContentFirst,
      isLoading: LoadingContentFirst,
      error: errorContentFirst,
    } = useFetchApi(`${http}${dataAPI.link_API.fetchNetflixOriginals}`);
    
    if (LoadingContentFirst) {
      
      return (
        <section>
          <p>Loading........</p>
        </section>
      );
    }
    if (errorContentFirst) {
      return (
        <section>
          <p>{errorContentFirst}</p>
        </section>
      );
    }
    // dataContentFirst.results.map(a=>{
    //   console.log(a.id)
    // })
    
    return(
      <div className="contentFirst contentAll">
            {dataContentFirst.results.map((data)=>{
              return(
              <RenderContentImg
                key={data.id}
                id={data.id}
                imgPoster={`${dataAPI.HttpImg}${data.poster_path}`}
                name={data.title?data.title:data.name}
                releaseDate={data.release_date}
                depcription={data.overview}
                vote={data.vote_average}
              />
              )
             })}
      </div>
        
           
        
    )

};
export default ContentFirst;
