import React from "react";
import RenderContentImg from "./RenderContentImg";

import { useContext } from "react";
import APIContext from "../store/APIContext";
const ContentThird=()=>{
    const dataAPI=useContext(APIContext)
    const http = "https://api.themoviedb.org/3";
      
        const {
          data: dataContentThird,
          isLoading: LoadingContentThird,
          error: errorContentThird,
        } = useFetchApi(`${http}${dataAPI.link_API.fetchTopRated}`);
        
        if (LoadingContentThird) {
          
          return (
            <section>
              <p>Loading........</p>
            </section>
          );
        }
        if (errorContentThird) {
          return (
            <section>
              <p>{errorContentThird}</p>
            </section>
          );
        }
        // dataContentThird.results.map(a=>{
        //   console.log(`${dataAPI.HttpImg}${a.poster_path}`)
        // })
        
        return(
            <React.Fragment>
            <h5>Xếp hạng cao</h5>
            <div className="contentThird contentAll">
              {dataContentThird.results.map((data) => {
                return (
                  <RenderContentImg
                    key={data.id}
                    id={data.id}
                    imgPbackdrop={`${dataAPI.HttpImg}${data.backdrop_path}`}
                    name={data.title ? data.title : data.name}
                    releaseDate={data.release_date}
                    depcription={data.overview}
                    vote={data.vote_average}
                  />
                );
              })}
            </div>
          </React.Fragment>
            
               
            
        )
    
    };
    export default ContentThird;
