import RenderResult from "./RenderResult";
import { useContext ,useEffect,useState} from "react";
import APIContext from "../store/APIContext";
//======================

const ResultList = () => {
  const dataSearch = useContext(APIContext);
  const[keySearch,setkeySearch]=useState('batman')
  const [dataApimovie, setdataApimovie] = useState([]);
  const [error, seterror] = useState(null);
  const [isLoading, setisLoading] = useState(true);
  useEffect(() => {
    

    const fetchApi = async () => {
      setisLoading(true);
      
        
        const response = await fetch(`
        https://august-bucksaw-357410-default-rtdb.firebaseio.com/abc.json`);

        const data = await response.json();
       
        
      
      setisLoading(false);
      setdataApimovie(data);
      
    };
    fetchApi()
  }, [keySearch]);
  
  if (isLoading) {
    return;
  }
  if(dataApimovie.results)
  
  console.log(dataApimovie.results)
  
  return (
    <div className="form_result">
      <h5>Search Result</h5>
      {!isLoading&&<div className="result_img">
        {dataApimovie.results.map((a) => (
          <RenderResult
            key={a.id}
            imgPoster={`
              https://image.tmdb.org/t/p/original${
                a.poster_path ? a.poster_path : a.backdrop_path
              }`}
          ></RenderResult>
        ))}
      </div>}
    </div>
  );
};
export default ResultList;
