import RenderBaner from "./RenderBaner";
// import "../../dist/index.css"
import React, { useContext } from "react";

import APIContext from "../store/APIContext";
//=========================
const Banner = () => {
  const dataAPI = useContext(APIContext);
  dataAPI.link_API[0].results

  
  const numberData=Math.floor(Math.random() * dataAPI.link_API[0].results.length - 1)
  
  return (
  
    <RenderBaner
      key={dataAPI.link_API[0].results[numberData].id}
      imgBackground={dataAPI.link_API[0].results[numberData].backdrop_path}
      title={dataAPI.link_API[0].results[numberData].original_title?dataAPI.link_API[0].results[numberData].original_title:dataAPI.link_API[0].results[numberData].original_name}
      depcription={dataAPI.link_API[0].results[numberData].overview}
      
    ></RenderBaner>
  );
};
export default Banner;
