import React, { useEffect, useState } from "react";

const MovieDetail = (props) => {
  const [dataMoviesId, setdataMoviesId] = useState([]);
  useEffect(() => {
    // const getData = [];

    const fetchApi = async () => {
      setisLoading(true);

      const response = await fetch(
        `https://api.themoviedb.org/3//movie/${props.id}/videos?api_key=6fc3d765745ff0bf0840cc0f79c98ab6`
      );

      const data = await response.json();
      setdataMoviesId(data);
    };
    setisLoading(false);

    fetchApi().catch((error) => {
      setisLoading(false);
      seterror(error.message);
    });
  }, []);

  console.log(props);
  console.log(dataMoviesId)
  return (
    <div className="abc">
      <iframe
        width="560"
        height="315"
        src="https://www.youtube.com/embed/RCuFBmMCZFs"
        title="YouTube video player"
        frameBorder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
      ></iframe>
    </div>
  );
};
export default MovieDetail;
