import React, { useState, useEffect,useContext } from "react";
import RenderMoviesList from "./RenderMoviesList";
import ClickContext from "../store/ClickContext";

//==================
const reducer = (state, action) => {
  switch (action) {
    case "fetchTrending":
      return "Xu hướng";
    case "fetchTopRated":
      return "Xếp hạng cao";
    case "fetchActionMovies":
      return "Hành động";
    case "fetchComedyMovies":
      return "Hài";
    case "fetchHorrorMovies":
      return "Kinh dị";
    case "fetchRomanceMovies":
      return "Lãng mạn";
    case "fetchDocumentaries":
      return "tài liệu";
    default:
      return "";
  }
};
const thinh = useContext(ClickContext)

  useEffect(()=>{
    console.log(thinh)
  },[thinh.clickTF])
const MoviesList = (props) => {
  
  // const [chaneTitle, dispatch] = useReducer(reducer, "");
  // console.log(props);
  const dataAPI = "https://image.tmdb.org/t/p/original";

  // if(props.title==='fetchNetflixOriginals'){
  //   settitleFrist(true)
  // }else{
  //   settitleFrist(false)
  // }

  const reducer = (title) => {
    switch (title) {
      case "fetchTrending":
        return "Xu hướng";
      case "fetchTopRated":
        return "Xếp hạng cao";
      case "fetchActionMovies":
        return "Hành động";
      case "fetchComedyMovies":
        return "Hài";
      case "fetchHorrorMovies":
        return "Kinh dị";
      case "fetchRomanceMovies":
        return "Lãng mạn";
      case "fetchDocumentaries":
        return "Tài liệu";
      default:
        return "";
    }
  };
  //  dispatch(props.title)
  // if(props.title==='fetchNetflixOriginals'){
  //   setchangeClass('contentFirst')
  // }
  return (
    <React.Fragment>
      <h5>{reducer(props.title)}</h5>
      <div
        className={`${
          props.title === "fetchNetflixOriginals"
            ? "contentFirst contentAll"
            : "contentAll"
        }`}
      >
        {props.data.map((data) => {
          return (
            <RenderMoviesList
              key={data.id}
              id={data.id}
              imgPoster={`
              ${dataAPI}${
                props.title === "fetchNetflixOriginals"
                  ? data.poster_path
                  : data.backdrop_path
                  ? data.backdrop_path
                  : data.poster_path
              }`}
              name={data.title ? data.title : data.name}
              releaseDate={data.release_date}
              depcription={data.overview}
              vote={data.vote_average}
            />
          );
        })}
      </div>

    </React.Fragment>
  );
};
export default MoviesList;
