import React,{useState,useEffect} from "react";
import MovieDetail from "../MovieDetail/MovieDetail";
//=====================
const RenderMoviesList = (props) => {
  const [onClickImg,setonClickImg]=useState(false)
  const [idOnClickPre,setidOnClickPre]=useState('')
    const clickImg=()=>{
        setonClickImg(!onClickImg)
        
        setidOnClickPre(props.id)
        // console.log(onClickImg)
    }
  return (
    <div className={props.name}>
      <img
        src={props.imgPoster}
        alt={props.name}
        onClick={clickImg}
      />

      {onClickImg &&<MovieDetail></MovieDetail>}
    </div>
  );
};
export default RenderMoviesList;
