import React from "react";
import RenderContentImg from "./RenderContentImg";
import useFetchApi from "../../hooks/use-fetchApi";
import { useContext } from "react";
import APIContext from "../store/APIContext";



//==================

const ContentEighth = () => {
  const dataAPI = useContext(APIContext);
  const http = "https://api.themoviedb.org/3";

  const {
    data: dataContentEighth,
    isLoading: LoadingContentEighth,
    error: errorContentEighth,
  } = useFetchApi(`${http}${dataAPI.link_API.fetchActionMovies}`);

  if (LoadingContentEighth) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (errorContentEighth) {
    return (
      <section>
        <p>{errorContentEighth}</p>
      </section>
    );
  }
  // dataContentEighth.results.map(a=>{
  //   console.log(`${dataAPI.HttpImg}${a.poster_path}`)
  // })

  return (
    <React.Fragment>
      <h5>Tài liệu</h5>
      <div className="contentEighth contentAll">
        {dataContentEighth.results.map((data) => {
          return (
            <RenderContentImg
              key={data.id}
              imgPbackdrop={`${dataAPI.HttpImg}${data.backdrop_path}`}
              name={data.title ? data.title : data.name}
              releaseDate={data.release_date}
              depcription={data.overview}
              vote={data.vote_average}
            />
          );
        })}
      </div>
    </React.Fragment>
  );
};
export default ContentEighth;
