import ClickContext from "./ClickContext";
import { useReducer ,useState} from "react";
// import reducer from "./reducer";
const init={
    id:'',
    clickImg:''
}
const reducer=(state,action)=>{
    switch (action.clickImg){
        case true :
            return {
                id:action.id,
                
            };
        case false:
            return{
                ...state
            }

    }
}

const ClickProvider=(props)=>{
    const [state,dispatch]=useReducer(reducer,init)
    // const [checkcClick,setcheckcClick]=useState(init)
    const checkClick=()=>{
        dispatch(!init.clickImg)
    }
    const clickcontext={
        id:'',
        clickImg:checkClick,
        clickTF:state
    }
    return(
        <ClickContext.Provider value={clickcontext}
        >
            {props.children}
        </ClickContext.Provider>
    )
}
export default ClickProvider;
