    id:'',
    clickImg:'',
    clickTF:''
}
const reducer=(state,action)=>{
    switch (action.clickImg){
        case true :
            return {
                id:action.id,
                clickTF:true
            };
        case false:
            return{
                ...state,
                clickTF:false
            }
        default:

    }
}

const ClickProvider=(props)=>{
    const [state,dispatch]=useReducer(reducer,init)
    // const [checkcClick,setcheckcClick]=useState(init)
    const checkClick=()=>{
        dispatch(!clickImg)
    }
    const clickcontext={
        id:'',
        clickImg:checkClick,
        clickTF:state.clickTF
    }
    return(
        <ClickContext.Provider value={clickcontext}
        >
            {props.children}
        </ClickContext.Provider>
    )
}
export default ClickProvider;
