import IconSearch from "./IconSearch";

//==========================
const RenderBaner = (props) => {
  const [navbar,setNavbar]=useState(false)
  
  
  let srcImg;
  if(!props.load){
    srcImg=`https://image.tmdb.org/t/p/original${props.imgBackground}`
  }
  else{srcImg=''}
  return (
    
    <div className="Banner">
        <img src={srcImg} alt={props.title}/>
      
      <div className="Banner">
        <h2><strong>{props.title}</strong></h2>
        <button className="btn btn-light">Play</button>
        <button className="btn btn-light">My list</button>
        <p>
          <strong>{props.depcription}</strong>
        </p>
      </div>
      {/* <iframe width="560" height="315" src="https://www.youtube.com/embed/hoGXuSwmTvw" title="YouTube video player" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe> */}
    </div>
  );
};

export default RenderBaner;
