import APIContext from "../store/APIContext";
import { useContext } from "react";
import useFetchApi from "../../hooks/use-fetchApi";
import RenderContentFirst from "./RenderContentFirst";
//======================
const ContentFirst=()=>{
const dataAPI=useContext(APIContext)
const http = "https://api.themoviedb.org/3";
  
    const {
      data: dataContentFirst,
      isLoading: LoadingContentFirst,
      error: errorContentFirst,
    } = useFetchApi(`${http}${dataAPI.link_API.fetchNetflixOriginals}`);
    
    if (LoadingContentFirst) {
      
      return (
        <section>
          <p>Loading........</p>
        </section>
      );
    }
    if (errorContentFirst) {
      return (
        <section>
          <p>{errorContentFirst}</p>
        </section>
      );
    }
    console.log(dataContentFirst)
    
    return(
      <div>
            {dataContentFirst.results.map((data)=>{
              <RenderContentFirst
                key={data.id}
                imgPoster={data.poster_path}
                name
              />
             })}
      </div>
        
           
        
    )

};
export default ContentFirst;
