import React, { useState, useEffect, useContext } from "react";

//=====================
const RenderMoviesList = (props) => {
  const [onClickImg, setonClickImg] = useState(false);

  // console.log(abc)
  // console.log(props.clickmethot)
  const clickImg = () => {
    setonClickImg(!onClickImg);
    useEffect(()=>{})
    props.onClickmethot(
      onClickImg,
      props.id,
      props.name,
      props.depcription,
      props.vote,
      props.releaseDate,
      props.imgBackdropDetail
    );
  };

  return (
    <div className={props.name}>
      <img src={props.imgPoster} alt={props.name} onClick={clickImg} />
    </div>
  );
};
export default RenderMoviesList;
