import RenderResult from "./RenderResult";
import { useContext ,useEffect,useState} from "react";
import APIContext from "../store/APIContext";
//======================

const ResultList = () => {
  const dataSearch = useContext(APIContext);
  const [dataApimovie, setdataApimovie] = useState([]);
  const [error, seterror] = useState(null);
  const [isLoading, setisLoading] = useState(true);
  useEffect(() => {
    const getData = [];

    const fetchApi = async () => {
      setisLoading(true);
      for (const key in requests) {
        
        const response = await fetch(``);

        const data = await response.json();
        data.key = key;
        getData.push(data);
      }
      setisLoading(false);

      setdataApimovie(getData);
    };
    fetchApi().catch((error) => {
      setisLoading(false);
      seterror(error.message);
    });
  }, []);
  if (isLoading) {
    return (
      <section>
        <p>Loading........</p>
      </section>
    );
  }
  if (error) {
    return (
      <section>
        <p>{error}</p>
      </section>
    );
  }

  
  return (
    <div className="form_result">
      <h5>Search Result</h5>
      <div className="result_img">
        {dataSearch.link_API[0].results.map((a) => (
          <RenderResult
            key={a.id}
            imgPoster={`
              https://image.tmdb.org/t/p/original${
                a.poster_path ? a.poster_path : a.backdrop_path
              }`}
          ></RenderResult>
        ))}
      </div>
    </div>
  );
};
export default ResultList;
