import IconSearch from "../header/IconSearch";
import { useState,useContext } from "react";
import Searchcontext from "../store/contextSearch";
//==================

const RenderSearch = (props) => {
  const ctxSearch=useContext(Searchcontext)
  const [inputFilm,setinputFilm]= useState('') 
  const inputHandler=(event)=>{
    setinputFilm(event.target.value)
    
  }
  const onResetHandler=(event)=>{
    setinputFilm('')
    // ctxSearch.btnReset({inputSearch:inputFilm})
    // console.log(inputFilm)
  }

  const onSubmitHandler=(event)=>{
    setinputFilm(event.target.value)
    ctxSearch.btnSearch({inputSearch:inputFilm})
    // console.log(inputFilm)
  }

  return (
    <div className="form_search">
      <div className="icon-search">
        <input type='text' onChange={inputHandler} value={inputFilm}></input>
        <IconSearch></IconSearch>
      </div>
      <div className="btn_search">
        <button className="btn btn-light" type="reset" onClick={onResetHandler}>
          RESET
        </button>
        <button className="btn btn-primary" type="submit"onClick={onSubmitHandler}>
          SEARCH
        </button>
      </div>
    </div>
  );
};
export default RenderSearch;
